import React from 'react';
import Link from 'next/link'
import Head from 'next/head'
import {loadFirebase} from '../lib/db.js'
export default class Index extends React.Component{
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.datatableRef = React.createRef();
        this.$datatable = null
        this.state = {
            edit_id : '',
            delete_id : '',
            JobName : '',
            JobEmail : '',
            JobPhone : '',
            JobAddress : '',
            JobStatus : '',
            MinSalary : '',
            MaxSalary : '',
            WorkingDays : '',
            WorkingHours : '',
            AgeRange : '',
            JpSkill : '',
            JobDescription : '',
            Qualification : '',
            PostedDate : '',
            cities : props.city,
            areaName : '',
            jobs : props.job || [],
            areas : props.area || [],
        };
    }
    
    componentDidMount(){
        this.initializeDatable()
    }
    initializeDatable(){
        this.$datatable=$(this.datatableRef.current).DataTable({
            order: [[3, "desc"]],
        });
    }
    refreshTable = () =>{
        this.$datatable.clear()
        const self = this;
        jobList
            .getInitialProps()
            .then((response)=>{
                this.setState({
                    jobs:response.job
                })
            }
        ).bind(this);
}

 //       const script = document.createElement("script");
 //       script.src = "/assets/js/core/sort.js";
 //       script.async = true;
    
 //       document.body.appendChild(script);
        
 //   }
    static async getInitialProps() {

        const firebase = await loadFirebase() 
        const jobQuerySnapshot = await firebase
            .firestore()
            .collection("job")
            .get();
        const jobs = jobQuerySnapshot.docs.map((doc) =>({
            ...doc.data(),
            id: doc.id,
        }));
        const areaQuerySnapshot = await firebase
            .firestore()
            .collection("area")
            .get();
        const areas = areaQuerySnapshot.docs.map((doc)=>({
            ...doc.data(),
            id: doc.id,
        }));
        const cityQuerySnapshot = await firebase
            .firestore()
            .collection("city")
            .get();
        const cities = cityQuerySnapshot.docs.map((doc)=>({
            ...doc.data(),
            id: doc.id,
        }));
            return {job : jobs, area : areas, city : cities};
    }

    passDeleteId=(id)=>{
        //$("#deleteComfirmModal").modal('show');
        //this.setState({delete_id : id})
        console.log(this.state.delete_id)
    };
    viewPassId=(id)=>{
        this.setState({edit_id : id})
        let firebase=loadFirebase()
        let data={}
        try {
            firebase.firestore().collection('job').doc(id).get().then((snapshot)=>{
                data=snapshot.data()
                console.log(data)
                this.setState({
                    JobName:data.JobName,
                    JobEmail:data.JobEmail,
                    JobPhone:data.JobPhone,
                    JobAddress:data.JobAddress,
                    areaName:data.areaName,
                    cityName:data.cityName,
                    JobStatus:data.JobStatus,
                    MinSalary:data.MinSalary,
                    MaxSalary:data.MaxSalary,
                    WorkingDays:data.WorkingDays,
                    WorkingHours:data.WorkingHours,
                    AgeRange:data.AgeRange,
                    JpSkill:data.JpSkill,
                    JobDescription:data.JobDescription,
                    Qualification:data.Qualification,
                    PostedDate:data.PostedDate,
                })
            })
            console.log("getting data")
        }catch(error){
            console.log(error)
        }
        console.log(this.state.JobName)
        //$("#viewModal").modal('show')
    };
    passEditId=(id)=>{
        this.setState({edit_id:id})
        // let firebase=loadFirebase()
        // let data={}
        // try{
        //     firebase.firestore().collection('job').doc(id).get().then((snapshot)=>{
        //         data=snapshot.data()
        //         console.log()
        //         console.log(data)
        //         this.setState({
        //             JobName:data.JobName,
        //             JobEmail:data.JobEmail,
        //             JobPhone:data.JobPhone,
        //             JobAddress:data.JobAddress,
        //             areaName:data.areaName,
        //             cityName:data.cityName,
        //             JobStatus:data.JobStatus,
        //             MinSalary:data.MinSalary,
        //             MaxSalary:data.MaxSalary,
        //             WorkingDays:data.WorkingDays,
        //             WorkingHours:data.WorkingHours,
        //             AgeRange:data.AgeRange,
        //             JpSkill:data.JpSkill,
        //             JobDescription:data.JobDescription,
        //             Qualification:data.Qualification,
        //             PostedDate:data.PostedDate,
        //         })
        //         this.getArea(data.areaID)
        //     })
        //     }catch(error){
        //         console.log(error)
        //     }
            //$("#jobEditModal").modal('show')
        };
        updateJob=()=>{
            let firebase=loadFirebase()
            try{
                firebase.firestore().collection('job').doc(this.state.edit_id).update({
                    JobName:this.state.JobName,
                    JobEmail:this.state.JobEmail,
                    JobPhone:this.state.JobPhone,
                    JobAddress:this.state.JobAddress,
                    areaName:this.state.areaName,
                    cityName:this.state.cityName,
                    JobStatus:this.state.JobStatus,
                    MinSalary:this.state.MinSalary,
                    MaxSalary:this.state.MaxSalary,
                    WorkingDays:this.state.WorkingDays,
                    WorkingHours:this.state.WorkingHours,
                    AgeRange:this.state.AgeRange,
                    JpSkill:this.state.JpSkill,
                    JobDescription:this.state.JobDescription,
                    Qualification:this.state.Qualification,
                    PostedDate:this.state.PostedDate,
                });
                this.refreshTable();
            }catch(error){
                console.log(error)
            }
        };
        handleChange=(event)=>{
            this.setState({[event.target.name]:event.target.value})
            if(event.target.name == "areaID"){}
        };
        deleteJob=()=>{
            let firebase=loadFirebase()
            try{
                firebase.firestore().collection('job')
                .doc(this.state.delete_id)
                .delete();
                console.log("Delete successful");
                this.decreaseJobCount(this.state.company)
                this.refreshTable();
            }catch(error){
                console.log(error)
            }
        };
        decreaseJobCount = (id) => {
            let data = {}
              try{
                  let firebase = loadFirebase()
                  firebase.firestore().collection('employer')
                  .doc(id)
                  .get()
                  .then((employerQuerySnapshot)=>{
                      data = employerQuerySnapshot.data();
                      firebase.firestore().collection('employer')
                        .doc(id)
                        .update({jobCount : data.jobCount-1}) 
                      console.log(data.jobCount-1) 
                  })
              }catch(error){
                  console.log(error)
              }
        }
        EmpName = (id) => {
            const employer = this.props.employers
            let name = ''
            employer.map(employers=>{
                if(employer.id == id){
                    name = employer.name
                }
            })
            return name
        }
        getLocation = (cityID, areaID) => {
            const city = this.props.city
            const area = this.props.area
            let cityName = ''
            let areaName = ''
    
            city && city.map(City=>{
                if(City.id == cityID){
                    cityName = City.cityName
                }
            })
            area && area.map(Area=>{
                if(Area.id == areaID){
                    areaName = Area.areaName
                }
            })
           return cityName + "," + areaName
        }
        getArea = (id) => {
            let area = {}
            try{
              db.collection('area')
                .doc(id)
                .get()
                .then((areaQuerySnapshot)=>{
                    area = areaQuerySnapshot.data();
                    this.setState({areaName : area.areaName})
              })
            }catch(error){
                console.log(error)
            }
        }
        form_clear = (event) => {
            this.setState(this.initial_state);
            let inputs, index;
            inputs = document.getElementsByTagName('input');
            for (index = 0; index < inputs.length; ++index) {
                inputs[index].value = ''
            }
            document.getElementById('JobAddress').value = '';
            document.getElementById('JobDescription').value = '';
      
            $("#JobStatus option[selected]").removeAttr("selected");    
            $("#JobStatus option:first").attr("selected","selected");
            $("#JpSkill option[selected]").removeAttr("selected");    
            $("#JpSkill option:first").attr("selected","selected");
            $("#cityName option[selected]").removeAttr("selected");    
            $("#cityName option:first").attr("selected","selected"); 
            $("#areaName option[selected]").removeAttr("selected");    
            $("#areaName option:first").attr("selected","selected");   
        };
        disable = (event) => {   
            $("#JobStatus option:first").attr("disabled","disabled"); 
            $("#JpSkill option:first").attr("disabled","disabled"); 
            $("#cityName option:first").attr("disabled","disabled");
            $("#areaName option:first").attr("disabled","disabled"); 
        }

    render(){
        const cities = this.state.cities
        const areas = this.state.areas
        return (
            <html>
                <Head>
                    <title>Golden Snitch | Admin</title>
                </Head>
                <body class="">
                <div class="modal fade" id="jobEditModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title" id="exampleModalLabel">Edit Job</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div class="modal-body">
                        <input value={this.state.JobName}></input>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" class="btn btn-primary">Save changes</button>
                    </div>
                    </div>
                </div>
                </div>

        <div className="modal fade" id="deleteConfirm" tabindex="-1" role="dialog" aria-hidden="true">
        <div className="modal-dialog" role="document">
            <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLabel">Hey Admin !!!</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div className="modal-body">
                Are You Sure to Delete the Job ?
            </div>
            <div className="modal-footer">
                <button type="button" className="btn" data-dismiss="modal" aria-label="Cancel">Cancel</button>  
                <button type="button" className="btn" data-dismiss="modal" onClick={this.deleteJob}>Delete</button>
            </div>
            </div>
        </div>
        </div>
                    <div class="wrapper ">
                        <div class="sidebar" data-color="red">
                            <div class="logo">
                                <img src="/assets/img/logoo.png" alt="Logo"/>
                                <a href="/user" class="simple-text logo-normal" style={{textAlign: "center"}}>
                                    Golden Snitch Admin
                                </a>
                            </div>
                            <div class="sidebar-wrapper" id="sidebar-wrapper">
                                <ul class="nav">
                                    <li>
                                        <a href="/index">
                                            <i class="now-ui-icons design_app"></i>
                                            <p>Dashboard</p>
                                        </a>
                                    </li>
                                    <li class="active">
                                        <a href="/tables">
                                            <i class="now-ui-icons design_bullet-list-67"></i>
                                            <p>Table List</p>
                                        </a>
                                    </li>
                                    <li>
                                        <a href="/user">
                                            <i class="now-ui-icons users_single-02"></i>
                                            <p>User Profile</p>
                                        </a>
                                    </li>
                                </ul>
                            </div>
                        </div>
                            <div class="main-panel" id="main-panel">
                                <nav class="navbar navbar-expand-lg navbar-transparent  bg-primary  navbar-absolute">
                                    <div class="container-fluid">
                                        <div class="navbar-wrapper">
                                            <div class="navbar-toggle">
                                                <button type="button" class="navbar-toggler">
                                                    <span class="navbar-toggler-bar bar1"></span>
                                                    <span class="navbar-toggler-bar bar2"></span>
                                                    <span class="navbar-toggler-bar bar3"></span>
                                                </button>
                                            </div>
                                            <a class="navbar-brand" style={{fontSize: "x-large"}}>Job Table</a>
                                        </div>
                                            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navigation" aria-controls="navigation-index" aria-expanded="false" aria-label="Toggle navigation">
                                                <span class="navbar-toggler-bar navbar-kebab"></span>
                                                <span class="navbar-toggler-bar navbar-kebab"></span>
                                                <span class="navbar-toggler-bar navbar-kebab"></span>
                                            </button>
                                        <div class="collapse navbar-collapse justify-content-end" id="navigation">
                                            <ul class="navbar-nav">
                                                <li class="nav-item">
                                                    <a class="nav-link" href="/employer">
                                                        Employer
                                                    </a>
                                                </li>
                                                <li class="nav-item active">
                                                    <a class="nav-link" href="/job">
                                                        Job
                                                    </a>
                                                </li>
                                            </ul>
                                        </div>
                                    </div>
                                </nav>                  
                                <div class="panel-header panel-header-sm">
                                </div>
                                <div class="content">
                                    <div class="row">
                                        <div class="col-md-12">
                                            <div class="card">
                                                <div class="card-header">
                                                    <h4 class="card-title"> Job</h4>
                                                </div>
                                                <div class="card-body">
                                                    <div class="table-responsive">
                                                        <table ref={this.datatableRef} class="table table-striped table-bordered" style={{width:100+"%"}}>
                                                            <thead style={{fontSize:12}}>
                                                                <tr>
                                                                    <th>Name</th>
                                                                    <th>Email</th>
                                                                    <th>Phone</th>
                                                                    <th>Address</th>
                                                                    <th>Area</th>
                                                                    <th>City</th>
                                                                    <th>Employment Status</th>
                                                                    <th>Salary Range</th>
                                                                    <th>Working Days</th>
                                                                    <th>Working Hours</th>
                                                                    <th>Age Range</th>
                                                                    <th>Minimum Japanese Skill</th>
                                                                    <th>Job Description</th>
                                                                    <th>Qualification</th>
                                                                    <th>Posted Date</th>
                                                                    <th>Actions</th>
                                                                </tr>
                                                            </thead>
                                                            <tbody>
                                                            {this.state.jobs.map(Job => (
                                                                <tr id={Job.id}>
                                                                    <td>{Job.JobName}</td>
                                                                    <td>{Job.JobEmail}</td>
                                                                    <td>{Job.JobPhone}</td>
                                                                    <td>{Job.JobAddress}</td>
                                                                    <td>{Job.areaName}</td>
                                                                    <td>{Job.cityName}</td>
                                                                    <td>{Job.JobStatus}</td>
                                                                    <td>{Job.MinSalary} ~ {Job.MaxSalary}</td>
                                                                    <td>{Job.WorkingDays}</td>
                                                                    <td>{Job.WorkingHours}</td>
                                                                    <td>{Job.AgeRange}</td>
                                                                    <td>{Job.JpSkill}</td>
                                                                    <td>{Job.JobDescription}</td>
                                                                    <td>{Job.Qualification}</td>
                                                                    <td>{Job.PostedDate}</td>
                                                                    <td>
                                                                    <button data-toggle="modal" data-target="#jobEditModal" onClick={()=>this.passEditId(Job.id)}> Edit </button>&nbsp;
                                                                    <button onClick={this.passDeleteId(Job.id)}>Delete</button>
                                                                    </td>
                                                                </tr>
                                                                ))
                                                                }
                                                        </tbody>
                                                        </table>
                                                    </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <footer class="footer">
                            <div class="container">
                                <div class="row">
                                    <div class="col-md-12">
                                        <div class="inner-content">
                                            Copyright©2020 GoldenSnitch
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </footer>
                    </div>
                </div>
            </body>
            </html>
        )
    };
